    enabled: true,
});
const github = new keycloak.oidc.GithubIdentityProvider("github", {
    realm: realm.id,
    clientId: githubIdentityProviderClientId,
    clientSecret: githubIdentityProviderClientSecret,
    trustEmail: true,
    syncMode: "IMPORT",
    extraConfig: {
        myCustomConfigKey: "myValue",
    },
});
